//======================================================================
/// \short QAnisotropicSolidVectorExpansionElement elements are 
/// linear/quadrilateral/brick-shaped Advection Diffusion elements with 
/// isoparametric interpolation for the function.
//======================================================================
template <unsigned DIM, unsigned NVECT, unsigned NNODE_1D>
 class QAnisotropicSolidVectorExpansionElement : 
 public virtual QElement<DIM,NNODE_1D>,
 public virtual AnisotropicSolidVectorExpansionEquations<DIM, NVECT>
 {

private:

 /// \short Static array of ints to hold number of variables at 
 /// nodes: Initial_Nvalue[n]
 static const unsigned Initial_Nvalue;
 
  public:


 ///\short  Constructor: Call constructors for QElement and 
 /// Advection Diffusion equations
 QAnisotropicSolidVectorExpansionElement() : QElement<DIM,NNODE_1D>(), 
  AnisotropicSolidVectorExpansionEquations<DIM, NVECT>()
  { }

 /// Broken copy constructor
 QAnisotropicSolidVectorExpansionElement(
  const QAnisotropicSolidVectorExpansionElement<DIM, NVECT,NNODE_1D>&  dummy) 
  {BrokenCopy::broken_copy("QAnisotropicSolidVectorExpansionElement");} 
 
 /// Broken assignment operator
 void operator=(const QAnisotropicSolidVectorExpansionElement<DIM, NVECT,NNODE_1D>&) 
  {BrokenCopy::broken_assign("QAnisotropicSolidVectorExpansionElement");}

 /// \short  Required  # of `values' (pinned or dofs) 
 /// at node n
 inline unsigned required_nvalue(const unsigned &n) const 
  {return Initial_Nvalue;}

 /// \short Output function:  
 ///  x,y,u   or    x,y,z,u
 void output(std::ostream &outfile)
  {AnisotropicSolidVectorExpansionEquations<DIM, NVECT>::output(outfile);}

 /// \short Output function:  
 ///  x,y,u   or    x,y,z,u at n_plot^DIM plot points
 void output(std::ostream &outfile, const unsigned &n_plot)
  {AnisotropicSolidVectorExpansionEquations<DIM, NVECT>::output(outfile,n_plot);}


 /// \short C-style output function:  
 ///  x,y,u   or    x,y,z,u
 void output(FILE* file_pt)
  {AnisotropicSolidVectorExpansionEquations<DIM, NVECT>::output(file_pt);}

 ///  \short C-style output function:  
 ///   x,y,u   or    x,y,z,u at n_plot^DIM plot points
 void output(FILE* file_pt, const unsigned &n_plot)
  {AnisotropicSolidVectorExpansionEquations<DIM, NVECT>::output(file_pt,n_plot);}

 /// \short Output function for an exact solution:
 ///  x,y,u_exact   or    x,y,z,u_exact at n_plot^DIM plot points
 void output_fct(std::ostream &outfile, const unsigned &n_plot,
                 FiniteElement::SteadyExactSolutionFctPt 
                 exact_soln_pt)
  {AnisotropicSolidVectorExpansionEquations<DIM, NVECT>::output_fct(outfile,n_plot,exact_soln_pt);}


 /// \short Output function for a time-dependent exact solution.
 ///  x,y,u_exact   or    x,y,z,u_exact at n_plot^DIM plot points
 /// (Calls the steady version)
 void output_fct(std::ostream &outfile, const unsigned &n_plot,
                 const double& time,
                 FiniteElement::UnsteadyExactSolutionFctPt 
                 exact_soln_pt)
  {AnisotropicSolidVectorExpansionEquations<DIM, NVECT>::output_fct(outfile,n_plot,time,exact_soln_pt);}


protected:

 /// Shape, test functions & derivs. w.r.t. to global coords. Return Jacobian.
 inline double dshape_and_dtest_eulerian_anisotropic_solid_vector_expansion(
  const Vector<double> &s, 
  Shape &psi, 
  DShape &dpsidx, 
  Shape &test, 
  DShape &dtestdx) const;
 
 /// \short Shape, test functions & derivs. w.r.t. to global coords. at
 /// integration point ipt. Return Jacobian.
 inline double dshape_and_dtest_eulerian_at_knot_anisotropic_solid_vector_expansion(
  const unsigned& ipt,
  Shape &psi, 
  DShape &dpsidx, 
  Shape &test,
  DShape &dtestdx) 
  const;

};

//Inline functions:


//======================================================================
/// \short Define the shape functions and test functions and derivatives
/// w.r.t. global coordinates and return Jacobian of mapping.
///
/// Galerkin: Test functions = shape functions
//======================================================================
template<unsigned DIM, unsigned NVECT, unsigned NNODE_1D>
double QAnisotropicSolidVectorExpansionElement<DIM, NVECT,NNODE_1D>::
 dshape_and_dtest_eulerian_anisotropic_solid_vector_expansion(const Vector<double> &s,
                                         Shape &psi, 
                                         DShape &dpsidx,
                                         Shape &test, 
                                         DShape &dtestdx) const
{
 //Call the geometrical shape functions and derivatives  
 double J = this->dshape_eulerian(s,psi,dpsidx);

 //Loop over the test functions and derivatives and set them equal to the
 //shape functions
 for(unsigned i=0;i<NNODE_1D;i++)
  {
   test[i] = psi[i]; 
   for(unsigned j=0;j<DIM;j++)
    {
     dtestdx(i,j) = dpsidx(i,j);
    }
  }
 
 //Return the jacobian
 return J;
}



//======================================================================
/// Define the shape functions and test functions and derivatives
/// w.r.t. global coordinates and return Jacobian of mapping.
///
/// Galerkin: Test functions = shape functions
//======================================================================
template<unsigned DIM, unsigned NVECT, unsigned NNODE_1D>
double QAnisotropicSolidVectorExpansionElement<DIM, NVECT,NNODE_1D>::
 dshape_and_dtest_eulerian_at_knot_anisotropic_solid_vector_expansion(
 const unsigned &ipt,
 Shape &psi, 
 DShape &dpsidx,
 Shape &test, 
 DShape &dtestdx) const
{
 //Call the geometrical shape functions and derivatives  
 double J = this->dshape_eulerian_at_knot(ipt,psi,dpsidx);

 //Set the test functions equal to the shape functions (pointer copy)
 test = psi;
 dtestdx = dpsidx;

 //Return the jacobian
 return J;
}


